import React from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import UserList from "./components/UserManagement/UserList";
import RoleList from "./components/RoleManagement/RoleList";

function App() {
  return (
    <Router>
      <div>
        <h1 style={{ textAlign: "center" }}>Admin Dashboard</h1>
        {/* Navigation Links */}
        <div style={{ textAlign: "center", margin: "20px 0" }}>
          <Link to="/users" style={{ margin: "0 15px" }}>Manage Users</Link>
          <Link to="/roles" style={{ margin: "0 15px" }}>Manage Roles</Link>
        </div>
        <Routes>
          <Route path="/" element={<h2 style={{ textAlign: "center" }}>Select an option above to manage users or roles</h2>} />
          <Route path="/users" element={<UserList />} />
          <Route path="/roles" element={<RoleList />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
