import React, { useState } from "react";
import { Button, TextField, DialogActions, DialogContent } from "@mui/material";

const EditRoleForm = ({ role, setRoles, onClose }) => {
  const [updatedRole, setUpdatedRole] = useState(role);

  const handleSave = () => {
    setRoles((prev) =>
      prev.map((r) => (r.id === role.id ? updatedRole : r))
    );
    onClose(); // Close the dialog after saving
  };

  return (
    <div>
      <DialogContent>
        <TextField
          label="Role Name"
          fullWidth
          value={updatedRole.name}
          onChange={(e) =>
            setUpdatedRole({ ...updatedRole, name: e.target.value })
          }
        />
        <TextField
          label="Permissions (comma-separated)"
          fullWidth
          value={updatedRole.permissions.join(", ")}
          onChange={(e) =>
            setUpdatedRole({
              ...updatedRole,
              permissions: e.target.value.split(", "),
            })
          }
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button variant="contained" onClick={handleSave}>
          Save
        </Button>
      </DialogActions>
    </div>
  );
};

export default EditRoleForm;
