import React, { useState } from "react";
import { Dialog } from "@mui/material";
import AddRoleForm from "./AddRoleForm";
import { Button, Table } from "react-bootstrap";

const RoleList = () => {
  const [roles, setRoles] = useState([
    { id: 1, name: "Admin", permissions: ["Read", "Write", "Delete"] },
    { id: 2, name: "Editor", permissions: ["Read", "Write"] },
  ]);
  const [open, setOpen] = useState(false);

  const deleteRole = (id) => {
    setRoles(roles.filter((role) => role.id !== id));
  };

  return (
    <div
      className="p-4"
      style={{
        backgroundColor: "#f8f9fa", // Light grey background
        minHeight: "100vh",
        borderRadius: "8px",
      }}
    >
      <h2 className="text-center text-primary mb-4">Role Management</h2>

      {/* Add Role Button */}
      <div className="text-center mb-3">
        <Button variant="success" size="lg" onClick={() => setOpen(true)}>
          Add Role
        </Button>
      </div>

      {/* Add Role Dialog */}
      <Dialog open={open} onClose={() => setOpen(false)}>
        <AddRoleForm setRoles={setRoles} onClose={() => setOpen(false)} />
      </Dialog>

      {/* Roles Table */}
      <Table striped bordered hover responsive>
        <thead className="bg-primary text-white">
          <tr>
            <th>Role</th>
            <th>Permissions</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {roles.map((role) => (
            <tr key={role.id}>
              <td>{role.name}</td>
              <td>{role.permissions.join(", ")}</td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  className="me-2"
                  onClick={() => {
                    // Logic for editing a role can be added later
                  }}
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => deleteRole(role.id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default RoleList;
