import React, { useState } from "react";
import { TableBody, TableCell, TableHead, TableRow, Dialog } from "@mui/material";
import AddUserForm from "./AddUserForm";
import EditUserForm from "./EditUserForm";
import { Button, Table } from "react-bootstrap";

const UserList = () => {
  const [users, setUsers] = useState([
    { id: 1, name: "John Doe", role: "Admin", status: "Active" },
    { id: 2, name: "Jane Smith", role: "Editor", status: "Inactive" },
  ]);
  const [openAdd, setOpenAdd] = useState(false);
  const [openEdit, setOpenEdit] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const deleteUser = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  return (
    <div>
      <h2>User Management</h2>
      <Button variant="contained" onClick={() => setOpenAdd(true)}>Add User</Button>
      <Dialog open={openAdd} onClose={() => setOpenAdd(false)}>
        <AddUserForm setUsers={setUsers} onClose={() => setOpenAdd(false)} />
      </Dialog>
      <Dialog open={openEdit} onClose={() => setOpenEdit(false)}>
        <EditUserForm
          user={currentUser}
          setUsers={setUsers}
          onClose={() => setOpenEdit(false)}
        />
      </Dialog>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell>Role</TableCell>
            <TableCell>Status</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {users.map(user => (
            <TableRow key={user.id}>
              <TableCell>{user.name}</TableCell>
              <TableCell>{user.role}</TableCell>
              <TableCell>{user.status}</TableCell>
              <TableCell>
                <Button
                 variant="warning" size="sm" className="me-2"
                  onClick={() => {
                    setCurrentUser(user);
                    setOpenEdit(true);
                  }}
                >
                  Edit
                </Button>
                <Button color="error" onClick={() => deleteUser(user.id)}>
                  Delete
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default UserList;
