import React, { useState } from "react";
import { Button, Table, TableBody, TableCell, TableHead, TableRow, Dialog } from "@mui/material";
import AddRoleForm from "./AddRoleForm";

const RoleList = () => {
  const [roles, setRoles] = useState([
    { id: 1, name: "Admin", permissions: ["Read", "Write", "Delete"] },
    { id: 2, name: "Editor", permissions: ["Read", "Write"] },
  ]);
  const [open, setOpen] = useState(false);

  const deleteRole = (id) => {
    setRoles(roles.filter(role => role.id !== id));
  };

  return (
    <div>
      <h2>Role Management</h2>
      <Button variant="contained" onClick={() => setOpen(true)}>Add Role</Button>
      <Dialog open={open} onClose={() => setOpen(false)}>
        <AddRoleForm setRoles={setRoles} onClose={() => setOpen(false)} />
      </Dialog>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Role</TableCell>
            <TableCell>Permissions</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {roles.map(role => (
            <TableRow key={role.id}>
              <TableCell>{role.name}</TableCell>
              <TableCell>{role.permissions.join(", ")}</TableCell>
              <TableCell>
                <Button variant="outlined">Edit</Button>
                <Button color="error" onClick={() => deleteRole(role.id)}>Delete</Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default RoleList;
